import React, { useReducer, useRef } from 'react';

const TaskContext = React.createContext(null);

const TLWR = () => {

    const inputRef = useRef();
    const initialState = {
        tasks: [],
        title: ''
    }



    const [tasks, dispatch] = useReducer((state = [], action) => {

        switch (action.type) {
            case 'ADD_TASK':
                return [
                    ...state, 
                    { id: state.length, title: action.title }
                ]

            case 'REMOVE_TASK':
                return state.filter((task, index) => index !== action.index)
            default:
                return state;
        }

    })

    const handleSubmit = (e) => {
        e.preventDefault();

        dispatch({
            type: 'ADD_TASK',
            title: inputRef.current.value,
        })
    }

    return (
        <div>
            <h1>Lista de tareas</h1>

            <form onSubmit={ handleSubmit }>
                <label htmlFor="title">Task</label>
                <input type="text" name='title' placeholder='Name your new task' ref={ inputRef } />
                <button type="submit">Add task</button>
            </form>

            <div className="tasks">
                {
                    tasks && tasks.map((task, index) => {
                        return (
                            <div className="task">
                                <p key={ index }>{ task.title }</p>
                                <button onClick={ () => dispatch({ type: 'REMOVE_TASK', index })}>
                                    Borrar
                                </button>
                            </div>
                        )
                    })
                }
            </div>

        </div>
    );
}

export default TLWR;
