import React, { useReducer, useRef } from 'react';

const TaskContext = React.createContext(null);

const TLWR = () => {

    const titleRef = useRef();
    const descriptionRef = useRef();

    const [tasks, dispatch] = useReducer((state = [], action) => {

        switch (action.type) {
            case 'ADD_TASK':
                return [
                    ...state, 
                    {   id: state.length, 
                        title: action.title,
                        description: action.description
                    }
                ]

            case 'REMOVE_TASK':
                return state.filter((task, indexTask) => indexTask !== action.indexTask)
            default:
                return state;
        }

    })

    const handleSubmit = (e) => {
        e.preventDefault();

        dispatch({
            type: 'ADD_TASK',
            title: titleRef.current.value,
            description: descriptionRef.current.value
        })

        titleRef.current.value = '';
    }

    return (
        <div>
            <h1>Lista de tareas</h1>

            <form onSubmit={ handleSubmit }>
                <label htmlFor="title">Task</label>
                <input type="text" name='title' placeholder='Name your new task' ref={ titleRef } />
                <label htmlFor="description">Description</label>
                <input type="text" name="description" id="description" ref={ descriptionRef } />
                <button type="submit">Add task</button>
            </form>

            <div className="tasks">
                {
                    tasks && tasks.map((task, indexTask, indexDescription, indexButton) => {
                        return (
                            <div className="task">
                                <p key={ indexTask }>{ task.title }</p><br></br>
                                <span key={ indexDescription }>{ task.description }</span>
                                <button key={ indexButton } onClick={ () => dispatch({ type: 'REMOVE_TASK', indexTask })}>
                                    Borrar
                                </button>
                            </div>
                        )
                    })
                }
            </div>

        </div>
    );
}

export default TLWR;
