import React, { useReducer, useRef } from 'react';

const TaskContext = React.createContext(null);

const initialState = [
    {
        id: '',
        title: '',
        description: '',
        isCompleted: false,
    }
]

const reducer = ( state, action ) => {

    switch (action.type) {
        case 'ADD_TASK':
            return [
                ...state,
                {
                    id: state.length,
                    title: action.title,
                    description: action.description,
                    isCompleted: false,
                }
            ];

        case 'REMOVE_TASK':
            return state.filter((task, index) => index !== action.index );

        case 'COMPLETE_TASK':
            return [
                ...state,
                {
                    isCompleted: true,
                }
            ]

        case 'FILTER_TASK':
            break;

        default:
            return state;
    }

}

const TLWR = () => {

    // Iniciamos las referencias para controlar los datos
    const titleRef = useRef();
    const descriptionRef = useRef();

    // Iniciamos useReducer
    const [tasks, dispatch] = useReducer(reducer, initialState);

    // Función para enviar tareas
    const handleSubmit = (e) => {
        e.preventDefault();

        dispatch({
            type: 'ADD_TASK',
            title: titleRef.current.value,
            description: descriptionRef.current.value
        })

        titleRef.current.value = '';
        descriptionRef.current.value = '';
    }



    return (
        <TaskContext.Provider value={ tasks }>

            <h1>Lista de tareas</h1>

            <form onSubmit={ handleSubmit }>
                <label htmlFor="title">Task</label>
                <input type="text" name='title' placeholder='Name your new task' ref={ titleRef } />
                <label htmlFor="description">Description</label>
                <input type="text" name="description" id="description" ref={ descriptionRef } />
                <button type="submit">Add task</button>
            </form>

            <div className="tasks" style={{ display: 'flex', flexDirection: 'column'}}>
                {
                    tasks && tasks.map((task, index) => {
                        return (
                            <div className="task" key={ index }>

                                <p>{ task.title }</p><br></br>

                                <span>{ task.description }</span>

                                <p>{ task.isCompleted ? 'Tarea completada' : 'Tarea pendiente' }</p>

                                <button onClick={ () => dispatch({ type: 'REMOVE_TASK', index })}>
                                    Borrar
                                </button>

                                <button onClick={ () => dispatch({type: 'COMPLETE_TASK', index}) }>
                                    Completar
                                </button>
                            </div>
                        )
                    })
                }
            </div>

        </TaskContext.Provider>
    );
}

export default TLWR;
