import React, { useState, useEffect} from 'react';

// Bringin Child Components to Container
import Task from '../pure/Task';
import TaskForm from '../pure/forms/TaskForm';

// Bringing MODELS to Component
import { TaskModel } from '../../models/taskModel.class';
import { LEVELS } from '../../models/levels.enum';

//Bringing STYLES to Component
import '../../styles/tasks.scss';
import '../../styles/task-form.scss';
import '../../styles/global.scss'


const TaskList = () => {


    // Initial State
    const [tasks, setTasks] = useState( [] );
    const [loading, setLoading] = useState(true);


    // Loading state with useEffect
    useEffect(() => {
        setTimeout( () => {
            setLoading(false);
        }, 2000)
    }, [tasks]);


    // A task getting completed 
    function completeTask(task) {
        const index = tasks.indexOf(task)
        const tempTask = [...tasks];
        tempTask[index].completed = !tempTask[index].completed;
        setTasks(tempTask);
    }

    // A task getting deleted
    function deleteTask(task) {
        const index = tasks.indexOf(task);
        const tempTask = [...tasks];
        tempTask.splice(index, 1);
        setTasks(tempTask);
    }

    // A task being added
    function addTask(task) {
        const tempTask = [...tasks];
        tempTask.push(task)
        setTasks(tempTask);
    }

    const Table = () => {
        return (
        <table>
            <thead>
                <tr>
                    <th scope='col'>Title</th>
                    <th scope='col'>Description</th>
                    <th scope='col'>Priority</th>
                    <th scope='col'>Actions</th>
                </tr>
            </thead>

            <tbody>

                { tasks.map((task, index) => {
                    return(
                        <Task
                            task={ task } 
                            key={ index } 
                            complete={ completeTask }
                            remove={ deleteTask } >
                        </Task>
                    )
                })}

            </tbody>
        </table> 
        )
    }

    let tasksTable;

    if(tasks.length > 0) {
        tasksTable = <Table></Table>
    } else {
        tasksTable = (
            <div>
                <h3>There are no tasks to show</h3>
                <h4>Go on, create one!</h4>
            </div>
        )
    }

    const loadingStyle = {
        color: 'gray',
        fontSize: '30px',
        fontWeight: 'bold',
    }


    return (
        <div className='task-list'>
            <div className={ tasks.length === 0 ? 'card empty' : 'card' }>

                <div className="card-body" data-mbd-perfect-scrollbar='true'  style={ { position: 'relative', height: '400px' } }>
                    {loading ? (<p style={loadingStyle}>Loading...</p>) : tasksTable}
                </div>

            </div>

            <TaskForm add = { addTask } length = { tasks.length } tasks = { tasks }></TaskForm>

            {/* <button 
                className='add-task contained-button'
            >
                <i className="bi bi-plus-lg"></i>
            </button> */}
        </div>
    );
};


export default TaskList;
