import React, { useReducer } from 'react';
import '../../styles/tasks.scss';
import '../../styles/task-form.scss';
import '../../styles/global.scss';
import Task from '../pure/Task';
import TaskForm from '../pure/forms/TaskForm';


const myContext = React.createContext(null);

// Actions 
const ADD = 'ADD';
const REMOVE = 'REMOVE';
const COMPLETE = 'COMPLETE';

// Initial State 
const initialState = {
    tasks: [],
    isCompleted: false,
    isLoading: false,
    isAddingTasks: false,
}

// Reducer
const taskListReducer = ( state, action ) => {

    switch(action.type) {
        case ADD:
            return {
                ...state,
                tasks: action.payload,
            }

        case REMOVE:
            return {
                ...state,
                tasks: action.payload
            }

        case COMPLETE:
            return {
                ...state,
                isCompleted: true,
            }

        default:
            return state;
    }

}

const TaskListWR = () => {

    const [state, dispatch] = useReducer(taskListReducer, initialState);

    return (
        <myContext.Provider value={state}>

            <div>
                <h1>
                    Your tasks
                </h1>

                <div className={ initialState.tasks.length === 0 ? 'card empty' : 'card '}>

                    { initialState.tasks.length === 0 ?

                        (
                            <div className='task-table'>
                                <h3>There are no tasks to show</h3>
                                <p>Go on, create one!</p>
                            </div>
                        ) 

                        : 

                        ( 
                            initialState.tasks.map((task, index) => {
                                return (
                                    <Task 
                                        task={ task }
                                        key={ index }
                                        complete={ dispatch({type: COMPLETE}) }
                                        remove={ dispatch({type: REMOVE}) } >
                                    </Task>
                                )
                            })
                        )
                    }

                    <button className='contained-button add-tasks'>
                        <i className="bi bi-plus-square-fill" style={{marginRight: '8px'}}></i>
                        Add task
                    </button>

                </div>

                <TaskForm 
                    add = { dispatch({type: ADD}) } 
                    length = { initialState.tasks.length } 
                    tasks = { initialState.tasks }>
                </TaskForm>

            </div>

        </myContext.Provider>
    );
}

export default TaskListWR;
